<script setup>
import {useForm,Link,router} from '@inertiajs/vue3'
import Layout from './../Components/Layout.vue'



const props=defineProps({
    item:Object,
    photo:String,
    categories:Array
})

const form=useForm({
    name: props.item.name,
    category_id: 1, // Assuming category_id is part of your form
    price: props.item.price, // Assuming price is a number field
    description: props.item.description,
    item_condition: props.item.item_condition,
    item_type: props.item.item_type,
    status: props.item.status,
    photo: null,
    owner_name: props.item.owner_name,
    owner_contact_number: props.item.owner_contact_number,
    owner_address: props.item.owner_address
})

const updateItem= () =>{
    router.post(`/admin/items/${props.item.id}`, {
    _method: 'put',
    name: form.name,
    category_id: 1, // Assuming category_id is part of your form
    price: form.price, // Assuming price is a number field
    description: form.description,
    item_condition: form.item_condition,
    item_type: form.item_type,
    status: form.status,
    photo: form.photo,
    owner_name: form.owner_name,
    owner_contact_number: form.owner_contact_number,
    owner_address: form.owner_address
})
}


